const{createMicrosoftGraphClient,TeamsFx}=require("@microsoft/teamsfx");async function showUserInfo(context,ssoToken,param){await context.sendActivity("Retrieving user information from Microsoft Graph ...");const teamsfx=(new TeamsFx).setSsoToken(ssoToken),graphClient=createMicrosoftGraphClient(teamsfx,["User.Read"]),me=await graphClient.api("/me").get();me?await context.sendActivity(`You're logged in as ${me.displayName} (${me.userPrincipalName})${me.jobTitle?`; your job title is: ${me.jobTitle}`:""}.`):await context.sendActivity("Could not retrieve profile information from Microsoft Graph.")}module.exports={showUserInfo};